import './productlistall.css';

import React, { useState } from 'react';

import Filter from '../../../';
import { ProductData } from '../../../util/ProductData';
import Productlistcard from '../../cards/productlist-card/Productlistcard';
import Footer from '../footer/Footer';

const ProductsListAll = () => {
    const [filteredProducts, setFilteredProducts] = useState(ProductData);

    const handleFilter = ({ category, brand, minPrice, maxPrice }) => {
        let filtered = ProductData;
        if (category) {
            filtered = filtered.filter(product => product.category === category);
        }
        if (brand) {
            filtered = filtered.filter(product => product.brand === brand);
        }
        if (minPrice) {
            filtered = filtered.filter(product => product.price >= minPrice);
        }
        if (maxPrice) {
            filtered = filtered.filter(product => product.price <= maxPrice);
        }
        setFilteredProducts(filtered);
    };

    const categories = [...new Set(ProductData.map(product => product.category))];
    const brands = [...new Set(ProductData.map(product => product.brand))];

    return (
        <section>
            <div className='my-2 py-4' id='featured'>
                <div className='container mt-5 py-5'>
                    <h3>Productos Disponibles</h3>
                    <hr className="mx-0"></hr>
                    <p>descripcion de la seccion</p>
                </div>

                <div className='row mx-auto container'>
                    <div className='col-md-3'>
                        <Filter categories={categories} brands={brands} onFilter={handleFilter} />
                    </div>
                    <div className='col-md-9'>
                        <div className='row'>
                            {filteredProducts.map((product) => {
                                return (
                                    <Productlistcard key={product.id} productData={product} />
                                )
                            })}
                        </div>
                    </div>
                </div>
            </div>
            <Footer />
        </section>
    );
};

export default ProductsListAll;
