import './filter.css';
import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';
const Filter =({ categories, brands, onFilter }) => {
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedBrand, setSelectedBrand] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');

  const handleFilter = () => {
    onFilter({ category: selectedCategory, brand: selectedBrand, minPrice, maxPrice });
  };

  return (
    <Form>
      <Form.Group controlId="categorySelect">
        <Form.Label>Categoría</Form.Label>
        <Form.Control as="select" value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
          <option value="">Todas</option>
          {categories.map((category, index) => (
            <option key={index} value={category}>{category}</option>
          ))}
        </Form.Control>
      </Form.Group>
      
      <Form.Group controlId="brandSelect">
        <Form.Label>Marca</Form.Label>
        <Form.Control as="select" value={selectedBrand} onChange={(e) => setSelectedBrand(e.target.value)}>
          <option value="">Todas</option>
          {brands.map((brand, index) => (
            <option key={index} value={brand}>{brand}</option>
          ))}
        </Form.Control>
      </Form.Group>
      
      <Form.Group controlId="minPrice">
        <Form.Label>Precio Mínimo</Form.Label>
        <Form.Control type="number" value={minPrice} onChange={(e) => setMinPrice(e.target.value)} />
      </Form.Group>
      
      <Form.Group controlId="maxPrice">
        <Form.Label>Precio Máximo</Form.Label>
        <Form.Control type="number" value={maxPrice} onChange={(e) => setMaxPrice(e.target.value)} />
      </Form.Group>
      
      <Button variant="primary" onClick={handleFilter}>
        Filtrar
      </Button>
    </Form>
  );
};
export default Filter;
