import './filter.css';

import React, { useState } from 'react';

const Filter =()=>{
    const [priceRange, setPriceRange] = useState([0, 1000]);

    const handlePriceChange = (event) => {
        const value = event.target.value;
        setPriceRange([0, value]);
    };
    return(
   <section className = "container ">
        <div className = "row">
            <div className = "col text-center my-2">
                <h1 className = "fs-2 color">Busqueda abanzada</h1>
                <div className = "underline mx-auto mt-3"></div>
            </div>
        </div>

        <div className = "row mt-3 mb-4 button-group filter-button-group">
            <div className = "col d-flex justify-content-center ">
                <button type = "button" data-filter = "*" className = "m-1">All</button>
                <button type = "button" data-filter = ".Audifonos" className = "m-1">Audífonos</button>
                <button type = "button" data-filter = ".Teclados" className = "m-1">Teclados</button>
                <button type = "button" data-filter = ".Teclados" className = "m-1">Computadores</button>
                <button type = "button" data-filter = ".Consolas" className = "m-1">Consolas</button>
                <button type = "button" data-filter = ".Perifericos" className = "m-1">Periféricos</button>
            </div>
        </div>
        <div className="row mt-3 mb-4">
                <div className="col d-flex justify-content-center">
                    <label htmlFor="priceRange" className="me-2">Price Range:</label>
                    <input 
                        type="range" 
                        id="priceRange" 
                        name="priceRange" 
                        min="200.000" 
                        max="1000000" 
                        step="10" 
                        className="form-range" 
                        value={priceRange[1]} 
                        onChange={handlePriceChange} 
                    />
                    <span id="priceValue" className="ms-2">${priceRange[0]} - ${priceRange[1]}</span>
                </div>
            </div>
    </section>
);
}
export default Filter;
